              weather1.humidity = aPayload.Ha.toFixed(1) + '%'
              // weather1.pressure = aPayload.pa.toFixed(0) + 'pa'
              weather1.brightness = aPayload.La.toFixed(0) + 'lx'
              weather1.textFooter = aPayload.pa.toFixed(0) + 'pa'
              // weather1.battery = aPayload.battery + '%'
              weather1.iWeather1State = this.stateOk
            } catch (error) {
